const DistBaseBlockTracker = require('eth-block-tracker/dist/BaseBlockTracker')
const runBaseTests = require('eth-block-tracker/test/base')
const runPollingTests = require('eth-block-tracker/test/polling')
const runSubscribeTests = require('eth-block-tracker/test/subscribe')

runBaseTests(test, 'source - BaseBlockTracker', SourceBaseBlockTracker)
